CREATE DEFINER=`owltie_ciis00`@`%` PROCEDURE `Insertar_preins`(
    IN firstName varchar(65),
    IN lastName varchar(85),
    IN dni varchar(15),
    IN cellphone varchar(15),
    IN email varchar(80),
    IN country varchar(40),
    IN city varchar(50),
    IN tipo varchar(20),
    IN anio varchar(20),
    IN institution varchar(50),
    IN grade varchar(30),
    IN voucher varchar(100)
)
BEGIN
	
    DECLARE idusu INT;
    DECLARE idtipo INT;
    DECLARE idinscrip INT;

    IF (tipo='Estudiante')
    THEN
    SET @idtipo=4;
    end if;
    IF (tipo='Profesional')
    THEN
    SET @idtipo=3;
    end if;
    
    
    INSERT INTO `usuarios` (`nombre_usuario`, `apellido_usuario`,	`dni_usuario`,
		`celular_usuario`, `email_usuario`, `id_tipo_usuario`)
	VALUES (firstName, lastName, dni, cellphone, email, @idtipo);
    
    select @idusu := id_usuario from usuarios where dni_usuario = dni order by id_usuario desc limit 1;
    
    IF(@idtipo = 3)
    THEN
    
    INSERT INTO `profesional` (`grado_profesional`, `id_usuario`, `pais_profesional`,
		`ciudad_profesional`)
	VALUES (grade, @idusu, country, city);
    
    end if;
    
    
    IF(@idtipo = 4)
    THEN
    
    INSERT INTO `estudiante` (`id_usuario`, `anio_estudiante`, `pais_estudiante`,
		`ciudad_estudiante`, `institucion_estudiante`)
	VALUES (@idusu, anio, country, city,	institution);
    
    end if;
    
    
    INSERT INTO `inscripcion` (`id_usuario`,`fecha_inscripcion`,`enlace_foto_voucher`,`id_actividad`)
	VALUES (@idusu,NOW(),voucher,2);
    
	SELECT @idinscrip := id_inscripcion from inscripcion where id_usuario = @idusu;
    
    INSERT INTO `pago_inscripcion` (`id_inscripcion`)
	VALUES (@idinscrip);
    
    INSERT INTO `notification`
	(`tipo_user`,
	`name_notify`,
    `date_notify`,
    `id_inscripcion`)
	VALUES
	(@idtipo,
	concat(firstName, ', se ha preinscrito'),
    NOW(),
    @idinscrip);
    
    
END ;;
